import React, { useState } from 'react';
import { Label, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';

import './Header.css';


const Header = () => {

  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen(prevState => !prevState);

  return (
    <div className="header-container">
      <Label>Select Code: </Label>

      <Dropdown isOpen={dropdownOpen} toggle={toggle}>
        <DropdownToggle caret>
          Code
          </DropdownToggle>
        <DropdownMenu>
          <DropdownItem header>All codes</DropdownItem>
          <DropdownItem value='green'/>
          <DropdownItem>Blue</DropdownItem>
          <DropdownItem>Red</DropdownItem>
          <DropdownItem>Yellow</DropdownItem>
        </DropdownMenu>
      </Dropdown>

    </div>
  );
}

export default Header;
