import React, {useState } from 'react';
import { Link } from 'react-router-dom';

import './Join.css';

const Join = () => {
  const [name, setName] = useState('');
  const [room, setRoom] = useState('');

  return(
    <div className="joinOuterContainer">
      <div className="joinInnerContainer">
        <h1 className="heading">Join</h1>
        <div><input onChange={(event) => setName(event.target.value)} placeholder="Name" className="joinInput" type="text"/></div>
        <div><input onChange={(event) => setRoom(event.target.value)} placeholder="Room" className="joinInput mt-20" type="text"/></div>
        <Link onClick={event => (!name || !room) ? event.preventDefault() : null} to={`/chat?name=${name}&room=${room}`}>
          <button className="button mt-20" type="submit">Sign In</button>
        </Link>
        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatem illum animi repellat quas. Eaque aspernatur molestiae eos error, dolorum fugit, expedita, quod cum minima, corporis distinctio ullam inventore impedit quas.</p>
      
      </div>
    </div>
  )
}

export default Join
