import React, {Component, useState} from 'react';
import {Link, withRouter} from 'react-router-dom';

import './Join.css';

class Join extends Component {
  state = {
    name: "",
    room: ""
};
  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    });
  };

  handleOnClick = (e) => {
    e.preventDefault();

    this.props.addNameAndRoom(this.state.name, this.state.room);
    this.props.history.push("/chat"); //this is now done in 'addNameAndRoom' -> this may not work at all.....
  };
  render() {
    return (
          <div className="joinOuterContainer">
            <div className="joinInnerContainer">
              <h1 className="heading">Join</h1>
              <div>
                <input type="text" id="name" className="joinInput" onChange={handleChange}/>
              </div>
              <div>
                <input type="text" id="room" className="joinInput mt-20" onChange={handleChange} />
              </div>
              <Link to="/chat" onClick={() => this.handleOnClick}>
                <button className="button mt-20" type="submit">Sign In</button>
              </Link>
            </div>
          </div>
      )}
}



