import React, {Component} from 'react';



import Join from './components/Join/Join';
import Chat from './components/Chat/Chat';


class App extends Component {
    state = {
        name: "",
        room: "",
        isLoggedIn: false
};

    //this function updates parent (app.js) state as expected
    addNameAndRoom = (name, room) => {
        this.setState({
            name: name,
            room: room
        }
        );
    };
    loggedIn = () => {
        this.setState({
            isLoggedIn: true
        }
        );
    };

    join = () => {
        return (
            <Join addNameAndRoom={this.addNameAndRoom} loggedIn={this.loggedIn}/>
        )
    };
    chat = () => {
        return (
        <Chat name={this.state.name} room={this.state.room} />
        )
    };

    render() {
        return (
            <div>
                {!this.state.isLoggedIn ? this.join : this.chat}
            </div>
        )
    }
}

export default App
