import React, {Component} from 'react';

import { BrowserRouter as Router, Route } from 'react-router-dom';

import Join from './components/Join/Join';
import Chat from './components/Chat/Chat';


class App extends Component {
    state = {
        name: "martin",
        room: "5"
};
    addNameAndRoom = (name, room) => {
        var newName = name;//these are correct
        var newRoom = room;
        this.state.setState({
            name: newName,
            room: newRoom
        });

        console.log("Clicked in addname:" + " " + this.state.name + " " + this.state.room);
    };

    render() {
        return (
            <Router>
                <Route exact path="/" render={() => <Join addNameAndRoom={this.addNameAndRoom}/>} />
                <Route path="/chat" render={() => <Chat name={this.state.name} room={this.state.room}/>} />
            </Router>
        )
    }
};

export default App
