import React from 'react';
export function highlight(color){
  //remove any format to avoid overlap issues
  document.execCommand('removeFormat', false, null);
  //save selected text
  var text = "";
  if (window.getSelection) {
      text = window.getSelection().toString();
  } else if (document.selection && document.selection.type !== "Control") {
      text = document.selection.createRange().text;
  }
  //create new span around the text
  let SpanElement =  React.createElement(
            "span",
            {
              style: { backgroundColor: "#0099FF"}, //Color Here
              id: "SpanID",
              className: "SpanClassName",
              onClick: () => doSomething()
            },text);
  // var span = document.createElement("span");
  // span.style.backgroundColor = color;
  // span.setAttribute('onclick', 'doSomething()');
  document.execCommand('insertHTML', false, SpanElement);
  console.log(SpanElement);
}

const doSomething = () => {
  alert('I was clicked!');
  console.log('I was clicked');
}




