import React, {useState} from 'react';
import {Link, withRouter} from 'react-router-dom';

import './Join.css';

function Join(addNameAndRoom) {
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  let handleChange = (e) => {
    if(e.target.id === "name"){
      setName(e.target.value)
    }
    else if(e.target.id === "room"){
      setRoom(e.target.value)
    }
  }

  let handleOnClick = (e) => {
    e.preventDefault();
    //console.log("Clicked:" + " "+this.state.name+" "+this.state.room); This works as it should
    addNameAndRoom(name, room);
    //props.history.push("/chat"); this is now done in 'addNameAndRoom' -> this may not work at all.....
  }
    return (
        <div className="joinOuterContainer">
          <div className="joinInnerContainer">
            <h1 className="heading">Join</h1>
            <div>
              <input type="text" id="name" className="joinInput" onChange={handleChange}/>
            </div>
            <div>
              <input type="text" id="room" className="joinInput mt-20" onChange={handleChange} />
            </div>
            <Link to="/chat" onClick={handleOnClick}>
              <button className="button mt-20" type="submit">Sign In</button>
            </Link>
          </div>
        </div>
    )
}



